create extension if not exists "uuid-ossp";create table if not exists public.integrations(id uuid primary key default uuid_generate_v4(),tenant_id text not null,kind text not null,access_token text,refresh_token text,expires_at timestamptz,metadata jsonb,created_at timestamptz not null default now(),updated_at timestamptz not null default now());create unique index if not exists integrations_tenant_kind_idx on public.integrations(tenant_id,kind);create index if not exists integrations_kind_idx on public.integrations(kind);create index if not exists integrations_tenant_idx on public.integrations(tenant_id);create index if not exists integrations_metadata_placeid_idx on public.integrations((metadata->>'place_id'))where kind='reviews';create index if not exists integrations_metadata_engines_idx on public.integrations((metadata->'engines'))where kind='visibility';alter table public.integrations enable row level security;create policy "service_role_full_access" on public.integrations for all using(auth.role()='service_role')